#include <stdint.h>

uint16_t *fb = (void*)0x6000000;
const int xsz = 240;
const int ysz = 160;

#define FRAME_SEL_BIT		0x10
#define BG2_ENABLE			0x400

typedef struct bmp{
	int x, y;
	int *data;
} bitmap;

void writeFbPixel(int pos){
	fb[pos]=31;
	return;
}

void printBitmap(bitmap a){
	if(a.y>ysz || a.x>xsz)
		return;

	int i=0;
	for(int y=0; y<a.y; y++){
		for(int x=0; x<a.x; x++){
			switch(a.data[i]){
				case 1:
					writeFbPixel(i);
					break;

				default:
					break;

				}
			i++;
		}
		i=i+(xsz-(a.x-1));
	}
	return;

}

